{
  "name": "tomato-tracker",
  "version": "1.0.0",
  "description": "A comprehensive tomato planting and production tracker",
  "main": "backend/server.js",
  "scripts": {
    "start": "node backend/server.js",
    "dev": "nodemon backend/server.js",
    "client": "cd frontend && npm start",
    "dev-all": "concurrently \"npm run dev\" \"npm run client\""
  },
  "keywords": ["tomato", "gardening", "tracker", "agriculture"],
  "author": "",
  "license": "MIT",
  "dependencies": {
    "express": "^4.18.2",
    "sqlite3": "^5.1.6",
    "cors": "^2.8.5",
    "multer": "^1.4.5-lts.1",
    "dotenv": "^16.3.1"
  },
  "devDependencies": {
    "nodemon": "^3.0.1",
    "concurrently": "^8.2.0"
  }
}

const express = require('express');
const cors = require('cors');
const path = require('path');
const multer = require('multer');
require('dotenv').config();

const db = require('./database');
const plantsRouter = require('./routes/plants');
const harvestsRouter = require('./routes/harvests');
const careLogsRouter = require('./routes/careLogs');
const statsRouter = require('./routes/stats');

const app = express();
const PORT = process.env.PORT || 5000;

app.use(cors());
app.use(express.json());
app.use('/uploads', express.static(path.join(__dirname, 'uploads')));

app.use('/api/plants', plantsRouter);
app.use('/api/harvests', harvestsRouter);
app.use('/api/care-logs', careLogsRouter);
app.use('/api/stats', statsRouter);

app.get('/api/health', (req, res) => {
  res.json({ status: 'OK', message: 'Tomato Tracker API is running' });
});

db.initializeDatabase().then(() => {
  app.listen(PORT, () => {
    console.log(`Server is running on port ${PORT}`);
  });
}).catch(err => {
  console.error('Failed to initialize database:', err);
  process.exit(1);
});

const sqlite3 = require('sqlite3').verbose();
const path = require('path');

const dbPath = path.join(__dirname, 'tomato_tracker.db');
const db = new sqlite3.Database(dbPath);

const initializeDatabase = () => {
  return new Promise((resolve, reject) => {
    db.serialize(() => {
      db.run(`CREATE TABLE IF NOT EXISTS plants (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        variety TEXT NOT NULL,
        planting_date DATE NOT NULL,
        location TEXT,
        status TEXT DEFAULT 'active',
        notes TEXT,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
      )`, (err) => {
        if (err) reject(err);
      });

      db.run(`CREATE TABLE IF NOT EXISTS harvests (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        plant_id INTEGER NOT NULL,
        harvest_date DATE NOT NULL,
        quantity INTEGER,
        weight_kg REAL,
        notes TEXT,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (plant_id) REFERENCES plants(id) ON DELETE CASCADE
      )`, (err) => {
        if (err) reject(err);
      });

      db.run(`CREATE TABLE IF NOT EXISTS care_logs (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        plant_id INTEGER NOT NULL,
        care_type TEXT NOT NULL,
        care_date DATE NOT NULL,
        notes TEXT,
        created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (plant_id) REFERENCES plants(id) ON DELETE CASCADE
      )`, (err) => {
        if (err) reject(err);
      });

      db.run(`CREATE TABLE IF NOT EXISTS photos (
        id INTEGER PRIMARY KEY AUTOINCREMENT,
        plant_id INTEGER NOT NULL,
        file_path TEXT NOT NULL,
        caption TEXT,
        upload_date DATETIME DEFAULT CURRENT_TIMESTAMP,
        FOREIGN KEY (plant_id) REFERENCES plants(id) ON DELETE CASCADE
      )`, (err) => {
        if (err) reject(err);
        else resolve();
      });
    });
  });
};

module.exports = {
  db,
  initializeDatabase
};

const express = require('express');
const router = express.Router();
const { db } = require('../database');

router.get('/', (req, res) => {
  const query = `
    SELECT p.*, 
           COUNT(DISTINCT h.id) as harvest_count,
           SUM(h.weight_kg) as total_weight_kg
    FROM plants p
    LEFT JOIN harvests h ON p.id = h.plant_id
    GROUP BY p.id
    ORDER BY p.created_at DESC
  `;

  db.all(query, [], (err, rows) => {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    res.json(rows);
  });
});

router.get('/:id', (req, res) => {
  const query = `SELECT * FROM plants WHERE id = ?`;

  db.get(query, [req.params.id], (err, row) => {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    if (!row) {
      res.status(404).json({ error: 'Plant not found' });
      return;
    }
    res.json(row);
  });
});

router.post('/', (req, res) => {
  const { variety, planting_date, location, status, notes } = req.body;

  if (!variety || !planting_date) {
    res.status(400).json({ error: 'Variety and planting date are required' });
    return;
  }

  const query = `
    INSERT INTO plants (variety, planting_date, location, status, notes)
    VALUES (?, ?, ?, ?, ?)
  `;

  db.run(query, [variety, planting_date, location, status || 'active', notes], function(err) {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    res.json({ id: this.lastID, ...req.body });
  });
});

router.put('/:id', (req, res) => {
  const { variety, planting_date, location, status, notes } = req.body;
  const query = `
    UPDATE plants 
    SET variety = ?, planting_date = ?, location = ?, status = ?, notes = ?,
        updated_at = CURRENT_TIMESTAMP
    WHERE id = ?
  `;

  db.run(query, [variety, planting_date, location, status, notes, req.params.id], function(err) {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    if (this.changes === 0) {
      res.status(404).json({ error: 'Plant not found' });
      return;
    }
    res.json({ id: req.params.id, ...req.body });
  });
});

router.delete('/:id', (req, res) => {
  db.run('DELETE FROM plants WHERE id = ?', [req.params.id], function(err) {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    if (this.changes === 0) {
      res.status(404).json({ error: 'Plant not found' });
      return;
    }
    res.json({ message: 'Plant deleted successfully' });
  });
});

module.exports = router;

const express = require('express');
const router = express.Router();
const { db } = require('../database');

router.get('/', (req, res) => {
  const { plant_id, start_date, end_date } = req.query;
  let query = `
    SELECT h.*, p.variety, p.location 
    FROM harvests h
    JOIN plants p ON h.plant_id = p.id
    WHERE 1=1
  `;
  const params = [];

  if (plant_id) {
    query += ' AND h.plant_id = ?';
    params.push(plant_id);
  }

  if (start_date) {
    query += ' AND h.harvest_date >= ?';
    params.push(start_date);
  }

  if (end_date) {
    query += ' AND h.harvest_date <= ?';
    params.push(end_date);
  }

  query += ' ORDER BY h.harvest_date DESC';

  db.all(query, params, (err, rows) => {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    res.json(rows);
  });
});

router.get('/plant/:plantId', (req, res) => {
  const query = `
    SELECT * FROM harvests 
    WHERE plant_id = ? 
    ORDER BY harvest_date DESC
  `;

  db.all(query, [req.params.plantId], (err, rows) => {
    if (err) {
      res.status(500).json({ error: err.message });
      return;
    }
    res.json(rows);
  });
});

