                      <div>üìÖ Planted: {format(new Date(plant.planting_date), 'MMM dd, yyyy')}</div>
                      <div>
                        üå± Status: 
                        <span style={{ 
                          color: plant.status === 'active' ? '#27ae60' : '#e74c3c',
                          fontWeight: '600'
                        }}> {plant.status}</span>
                      </div>
                    </div>
                  </div>
                  <button
                    onClick={(e) => {
                      e.preventDefault();
                      deletePlant(plant.id);
                    }}
                    className="btn btn-danger"
                    style={{ padding: '0.25rem 0.5rem', fontSize: '0.875rem' }}
                  >
                    Delete
                  </button>
